const EDGES: Edge[] = [
  { id: 'e1', source: 'a1', target: 'a6', type: 'success', activityLevel: 'high' },
  { id: 'e2', source: 'a2', target: 'a6', type: 'success', activityLevel: 'medium' },
  { id: 'e3', source: 'a4', target: 'a8', type: 'failed', activityLevel: 'low' },
